import React, { useState } from 'react';
import FileBase from 'react-file-base64';
import { TextField, Typography, Button, Paper } from '@material-ui/core'
import useStyles from './styles'
import { useDispatch } from 'react-redux';
import { createPost } from '../../actions/posts';

const Form = () => {
    const [postData, setPostData] = useState({creator: "", title: "", message: "", tags: "oiwej", selectedFile: ""});

    // const [homoText, setHomoText] = useState("hom")
    //  [  the value,      the function to call when you want to update the state/value  ]

    const [homoText, setHomoText] = useState("homo")

    console.log("--- current state ", homoText)

    console.log(" updating the state using updateState", updateState("no homo"))

    console.log("--- current state ", homoText)

    //  we have a value
    //  we need a way to update that value

    const classes = useStyles();
    const dispatch = useDispatch();

    const handleSubmit = (e) => {
        e.preventDefault(); 
        dispatch(createPost(postData));
    };

    const clear = () => {

    };

    const handleInput = (event) => {
        console.log(event)
    }

    return (
        <Paper className = {classes.paper}>
        <form autoComplete='off' noValidate className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit}>
        <Typography variant='h6'>Creating memories</Typography>
        <TextField name='creator' variant='outlined' label ='Creator' fullWidth value={postData.creator} onChange={(e) => setPostData({...postData, creator : e.target.value})}/> {/* Different than the video */}
        <TextField name='title' variant='outlined' label ='Title' fullWidth value={postData.title} onChange={(e) => setPostData({ ...postData, title : e.target.value})}/> {/* Different than the video */}
        <TextField name='message' variant='outlined' label ='Message' fullWidth value={postData.message} onChange={(e) => setPostData({ ...postData, message : e.target.value})}/> {/* Different than the video */}

         {/* Different than the video */}
        <TextField 
            name='tags' 
            variant='outlined' 
            label ='Tags'
            fullWidth
            placeholder='Enter this shit'
            value={postData.tags}
            // onChange={(e) => setPostData({ ...postData, tags : e.target.value})} 
        />

        <label>owinefoiwj</label>
        <input value={homoText} onChange={handleInput} ></input>


        <div className={classes.fileInput}><FileBase type = "file" multiple = {false} onDone={({base64}) => setPostData({ ...postData, selectedFile: base64 })}/></div>
        <Button className={classes.buttonSubmit} variant= 'container' color='primary' size='large' type='submit' fullWidth>Submit</Button>
        <Button  variant= 'contained' color='secondary' size='small' onClick={clear} fullWidth>Clear</Button>
        </form>
        </Paper>
    );
}

export default Form;
